defmodule Santa do
  def travel(_str, -1, position), do: position - 1

  def travel("(" <> str, floor, position) do
    travel(str, floor + 1, position + 1)
  end

  def travel(")" <> str, floor, position) do
    travel(str, floor - 1, position + 1)
  end
end

